import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Layers, 
  Image, 
  Type, 
  Mail, 
  Star,
  ChevronDown,
  ChevronRight,
  FileText,
  Grid3X3
} from 'lucide-react';
import { useWebsiteBuilder } from '../context/WebsiteBuilderContext';
import ComponentBlock from './ComponentBlock';
import WebsiteStats from './WebsiteStats';

const Sidebar = () => {
  const { addComponent, clearWebsite, components, updateComponents } = useWebsiteBuilder();
  const [activeTab, setActiveTab] = useState('components');
  const [expandedSections, setExpandedSections] = useState({
    basic: true,
    media: true,
    forms: true,
  });

  const toggleSection = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const componentTemplates = {
    'Hero Sections': [
      {
        type: 'hero',
        title: 'Modern Hero',
        subtitle: 'Create stunning hero sections with gradients and animations',
        buttonText: 'Get Started',
        background: 'bg-gradient-to-r from-blue-600 to-purple-600',
        icon: 'üéØ'
      },
      {
        type: 'hero-split',
        title: 'Split Hero',
        subtitle: 'Two-column hero with image and content',
        buttonText: 'Learn More',
        imageUrl: 'https://images.unsplash.com/photo-1551434678-e076c223a692?w=600&h=400&fit=crop',
        icon: 'üìê'
      },
      {
        type: 'hero-video',
        title: 'Video Hero',
        subtitle: 'Hero section with video background',
        buttonText: 'Watch Demo',
        videoUrl: 'https://sample-videos.com/zip/10/mp4/SampleVideo_1280x720_1mb.mp4',
        overlayColor: 'rgba(0,0,0,0.4)',
        icon: 'üé¨'
      },
      {
        type: 'image-text-overlay',
        title: 'Image Overlay Hero',
        subtitle: 'Hero with background image and text overlay',
        imageUrl: 'https://images.unsplash.com/photo-1497366216548-37526070297c?w=1200&h=500&fit=crop',
        overlayColor: 'rgba(0,0,0,0.5)',
        height: '600px',
        icon: 'üñºÔ∏è'
      }
    ],
    'Content Sections': [
      {
        type: 'text',
        title: 'Text Block',
        subtitle: 'Simple text content section',
        content: 'This is a sample text block. You can edit this content to add your own text.',
        icon: 'üìù'
      },
      {
        type: 'text-two-column',
        title: 'Two Column Text',
        subtitle: 'Content split into two columns',
        leftContent: { title: 'Left Column', content: 'Content for the left column goes here.' },
        rightContent: { title: 'Right Column', content: 'Content for the right column goes here.' },
        icon: 'üìÑ'
      },
      {
        type: 'text-with-image',
        title: 'Text with Image',
        subtitle: 'Text content with accompanying image',
        content: 'This section combines text content with a relevant image to create engaging content.',
        imageUrl: 'https://images.unsplash.com/photo-1551434678-e076c223a692?w=600&h=400&fit=crop',
        imagePosition: 'right',
        icon: 'üñºÔ∏è'
      },
      {
        type: 'video-section',
        title: 'Video Section',
        subtitle: 'Embedded video content',
        videoUrl: 'https://sample-videos.com/zip/10/mp4/SampleVideo_1280x720_1mb.mp4',
        icon: 'üé•'
      }
    ],
    'Feature Sections': [
      {
        type: 'grid',
        title: 'Feature Grid',
        subtitle: 'Showcase your key features',
        features: [
          { icon: '‚ö°', title: 'Fast Performance', description: 'Lightning-fast loading times' },
          { icon: 'üîí', title: 'Secure', description: 'Enterprise-grade security' },
          { icon: 'üì±', title: 'Responsive', description: 'Works on all devices' },
