import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Menu, 
  Palette, 
  Sun, 
  Moon, 
  User, 
  Settings, 
  HelpCircle,
  FileText,
  Download,
  Upload,
  Copy,
  Share2
} from 'lucide-react';
import { useTheme } from '../context/ThemeContext';
import { useWebsiteBuilder } from '../context/WebsiteBuilderContext';

const TopNavigation = ({ sidebarOpen, setSidebarOpen }) => {
  const { isDark, toggleTheme, colorPalette, setColorPalette } = useTheme();
  const { exportAsJSON, importFromJSON, duplicateWebsite, getWebsiteStats } = useWebsiteBuilder();
  const [showUserMenu, setShowUserMenu] = useState(false);
  const [showColorMenu, setShowColorMenu] = useState(false);
  const [projectName, setProjectName] = useState('My Website');

  const colorPalettes = [
    { name: 'Blue', value: 'blue', colors: ['#0ea5e9', '#0284c7', '#0369a1'] },
    { name: 'Purple', value: 'purple', colors: ['#9333ea', '#7c3aed', '#6d28d9'] },
    { name: 'Green', value: 'green', colors: ['#16a34a', '#15803d', '#166534'] },
    { name: 'Red', value: 'red', colors: ['#dc2626', '#b91c1c', '#991b1b'] },
    { name: 'Orange', value: 'orange', colors: ['#ea580c', '#c2410c', '#9a3412'] },
    { name: 'Pink', value: 'pink', colors: ['#db2777', '#be185d', '#9d174d'] }
  ];

  const handleFileImport = (event) => {
    const file = event.target.files[0];
    if (file) {
      importFromJSON(file)
        .then(() => {
          alert('Website imported successfully!');
        })
        .catch((error) => {
          alert('Error importing website: ' + error.message);
        });
    }
  };

  const handleDuplicate = () => {
    duplicateWebsite();
    alert('Website duplicated successfully!');
  };

  const handleExportBackup = () => {
    exportAsJSON();
  };

  const handleShare = () => {
    const stats = getWebsiteStats();
    const shareText = `Check out my website built with Website Builder! It has ${stats.totalComponents} components and ${stats.estimatedWordCount} words.`;
    
    if (navigator.share) {
      navigator.share({
        title: projectName,
        text: shareText,
        url: window.location.href
      });
    } else {
      navigator.clipboard.writeText(shareText);
      alert('Share text copied to clipboard!');
    }
  };

  return (
    <div className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 px-3 md:px-4 py-3 flex items-center justify-between h-16">
      {/* Left side */}
      <div className="flex items-center space-x-2 md:space-x-4">
        <button
          onClick={() => setSidebarOpen(!sidebarOpen)}
          className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
        >
          <Menu size={20} />
        </button>
        
        <div className="flex items-center space-x-2 md:space-x-3">
          <input
            type="text"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            className="text-base md:text-lg font-semibold bg-transparent border-none outline-none text-gray-900 dark:text-white min-w-0 max-w-32 md:max-w-none"
            placeholder="Project Name"
          />
          <span className="hidden sm:block text-sm text-gray-500 dark:text-gray-400">
            Website Builder
          </span>
        </div>
      </div>

      {/* Center - Quick Actions */}
      <div className="hidden md:flex items-center space-x-2">
        <button
          onClick={handleDuplicate}
          className="flex items-center space-x-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
          title="Duplicate Website"
        >
          <Copy size={16} />
          <span>Duplicate</span>
        </button>
        
        <button
          onClick={handleExportBackup}
          className="flex items-center space-x-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
          title="Export Backup"
        >
          <Download size={16} />
          <span>Backup</span>
        </button>
        
        <label className="flex items-center space-x-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors cursor-pointer">
          <Upload size={16} />
          <span>Import</span>
          <input
            type="file"
            accept=".json"
            onChange={handleFileImport}
            className="hidden"
          />
        </label>
        
        <button
          onClick={handleShare}
          className="flex items-center space-x-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
          title="Share Website"
        >
          <Share2 size={16} />
          <span>Share</span>
        </button>
      </div>

      {/* Right side */}
      <div className="flex items-center space-x-1 md:space-x-2">
        {/* Color Palette Selector */}
        <div className="relative">
          <button
            onClick={() => setShowColorMenu(!showColorMenu)}
            className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
            title="Color Palette"
          >
            <Palette size={20} />
          </button>
          
          <AnimatePresence>
            {showColorMenu && (
              <motion.div
                initial={{ opacity: 0, scale: 0.95, y: -10 }}
                animate={{ opacity: 1, scale: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.95, y: -10 }}
                className="absolute right-0 top-full mt-2 w-48 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-50"
              >
                <div className="p-3">
                  <h3 className="text-sm font-medium text-gray-900 dark:text-white mb-3">
                    Color Palette
                  </h3>
                  <div className="space-y-2">
                    {colorPalettes.map((palette) => (
                      <button
                        key={palette.value}
                        onClick={() => {
                          setColorPalette(palette.value);
                          setShowColorMenu(false);
                        }}
                        className={`w-full flex items-center space-x-3 p-2 rounded-md transition-colors ${
                          colorPalette === palette.value
                            ? 'bg-blue-50 dark:bg-blue-900/20 text-blue-600 dark:text-blue-400'
                            : 'hover:bg-gray-50 dark:hover:bg-gray-700'
                        }`}
                      >
                        <div className="flex space-x-1">
                          {palette.colors.map((color, index) => (
                            <div
                              key={index}
                              className="w-4 h-4 rounded-full border border-gray-200 dark:border-gray-600"
                              style={{ backgroundColor: color }}
                            />
                          ))}
                        </div>
                        <span className="text-sm">{palette.name}</span>
                      </button>
                    ))}
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        {/* Theme Toggle */}
        <button
          onClick={toggleTheme}
          className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
          title={isDark ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
        >
          {isDark ? <Sun size={20} /> : <Moon size={20} />}
        </button>

        {/* User Menu */}
        <div className="relative">
          <button
            onClick={() => setShowUserMenu(!showUserMenu)}
            className="flex items-center space-x-2 p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
          >
            <div className="w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center">
              <User size={16} className="text-white" />
            </div>
          </button>
          
          <AnimatePresence>
            {showUserMenu && (
              <motion.div
                initial={{ opacity: 0, scale: 0.95, y: -10 }}
                animate={{ opacity: 1, scale: 1, y: 0 }}
                exit={{ opacity: 0, scale: 0.95, y: -10 }}
                className="absolute right-0 top-full mt-2 w-48 bg-white dark:bg-gray-800 rounded-lg shadow-lg border border-gray-200 dark:border-gray-700 z-50"
              >
                <div className="p-2">
                  <div className="px-3 py-2 text-sm text-gray-500 dark:text-gray-400 border-b border-gray-200 dark:border-gray-700">
                    Signed in as User
                  </div>
                  
                  <button className="w-full flex items-center space-x-3 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-md transition-colors">
                    <Settings size={16} />
                    <span>Settings</span>
                  </button>
                  
                  <button className="w-full flex items-center space-x-3 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-md transition-colors">
                    <FileText size={16} />
                    <span>Documentation</span>
                  </button>
                  
                  <button className="w-full flex items-center space-x-3 px-3 py-2 text-sm text-gray-700 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-md transition-colors">
                    <HelpCircle size={16} />
                    <span>Help & Support</span>
                  </button>
                  
                  <div className="border-t border-gray-200 dark:border-gray-700 mt-2 pt-2">
                    <button className="w-full flex items-center space-x-3 px-3 py-2 text-sm text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-900/20 rounded-md transition-colors">
                      <span>Sign Out</span>
                    </button>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>

      {/* Mobile Quick Actions */}
      <div className="md:hidden flex items-center space-x-2">
        <button
          onClick={handleShare}
          className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
          title="Share"
        >
          <Share2 size={20} />
        </button>
      </div>
    </div>
  );
};

export default TopNavigation;
