import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';

const WebsiteBuilderContext = createContext();

export const useWebsiteBuilder = () => {
  const context = useContext(WebsiteBuilderContext);
  if (!context) {
    throw new Error('useWebsiteBuilder must be used within a WebsiteBuilderProvider');
  }
  return context;
};

export const WebsiteBuilderProvider = ({ children }) => {
  const [components, setComponents] = useState(() => {
    const saved = localStorage.getItem('websiteComponents');
    return saved ? JSON.parse(saved) : [];
  });

  const [selectedComponent, setSelectedComponent] = useState(null);
  const [history, setHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [isDirty, setIsDirty] = useState(false);

  // Auto-save functionality
  useEffect(() => {
    if (components.length > 0) {
      // Clean components for localStorage - remove React-specific properties
      const cleanComponents = components.map(comp => ({
        id: comp.id,
        type: comp.type,
        title: comp.title,
        subtitle: comp.subtitle,
        content: comp.content,
        buttonText: comp.buttonText,
        background: comp.background,
        backgroundSecondary: comp.backgroundSecondary,
        images: comp.images,
        features: comp.features
      }));
      
      localStorage.setItem('websiteComponents', JSON.stringify(cleanComponents));
      setIsDirty(false);
    }
  }, [components]);

  // History management
  const addToHistory = useCallback((newComponents) => {
    const newHistory = history.slice(0, historyIndex + 1);
    
    // Clean components for serialization - remove React-specific properties
    const cleanComponents = newComponents.map(comp => ({
      id: comp.id,
      type: comp.type,
      title: comp.title,
      subtitle: comp.subtitle,
      content: comp.content,
      buttonText: comp.buttonText,
      background: comp.background,
      backgroundSecondary: comp.backgroundSecondary,
      images: comp.images,
      features: comp.features
    }));
    
    newHistory.push(JSON.stringify(cleanComponents));
    
    // Limit history to 50 states
    if (newHistory.length > 50) {
      newHistory.shift();
    }
    
    setHistory(newHistory);
    setHistoryIndex(newHistory.length - 1);
  }, [history, historyIndex]);

  const updateComponents = useCallback((newComponents) => {
    setComponents(newComponents);
    addToHistory(newComponents);
    setIsDirty(true);
  }, [addToHistory]);

  const addComponent = useCallback((component) => {
    const newComponents = [...components, { ...component, id: Date.now().toString() }];
    updateComponents(newComponents);
  }, [components, updateComponents]);

  const updateComponent = useCallback((id, updates) => {
    const newComponents = components.map(comp => 
      comp.id === id ? { ...comp, ...updates } : comp
    );
    updateComponents(newComponents);
  }, [components, updateComponents]);

  const deleteComponent = useCallback((id) => {
    const newComponents = components.filter(comp => comp.id !== id);
    updateComponents(newComponents);
    if (selectedComponent?.id === id) {
      setSelectedComponent(null);
    }
  }, [components, updateComponents, selectedComponent]);

  const duplicateComponent = useCallback((id) => {
    const component = components.find(comp => comp.id === id);
    if (component) {
      const duplicated = { ...component, id: Date.now().toString() };
      const index = components.findIndex(comp => comp.id === id);
      const newComponents = [...components];
      newComponents.splice(index + 1, 0, duplicated);
      updateComponents(newComponents);
    }
  }, [components, updateComponents]);

  const moveComponent = useCallback((fromIndex, toIndex) => {
    const newComponents = [...components];
    const [movedComponent] = newComponents.splice(fromIndex, 1);
    newComponents.splice(toIndex, 0, movedComponent);
    updateComponents(newComponents);
  }, [components, updateComponents]);

  // Undo/Redo functionality
  const canUndo = historyIndex > 0;
  const canRedo = historyIndex < history.length - 1;

  const undo = useCallback(() => {
    if (canUndo) {
      const newIndex = historyIndex - 1;
      setHistoryIndex(newIndex);
      setComponents(JSON.parse(history[newIndex]));
      setIsDirty(false);
    }
  }, [canUndo, historyIndex, history]);

  const redo = useCallback(() => {
    if (canRedo) {
      const newIndex = historyIndex + 1;
      setHistoryIndex(newIndex);
      setComponents(JSON.parse(history[newIndex]));
      setIsDirty(false);
    }
  }, [canRedo, historyIndex, history]);

  // Clear website
  const clearWebsite = useCallback(() => {
    setComponents([]);
    setSelectedComponent(null);
    setHistory([]);
    setHistoryIndex(-1);
    setIsDirty(false);
    localStorage.removeItem('websiteComponents');
  }, []);

  // Export functionality
  const exportWebsite = useCallback(() => {
    const html = generateHTML(components);
    const blob = new Blob([html], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'website.html';
    a.click();
    URL.revokeObjectURL(url);
  }, [components]);

  const generateHTML = (comps) => {
    const componentHTML = comps.map(comp => {
      switch (comp.type) {
        case 'hero':
          return `
            <section class="hero-section" style="background: linear-gradient(135deg, ${comp.background || '#667eea'} 0%, ${comp.backgroundSecondary || '#764ba2'} 100%); padding: 80px 20px; text-align: center; color: white;">
              <div class="container" style="max-width: 1200px; margin: 0 auto;">
                <h1 style="font-size: 3rem; margin-bottom: 1rem; font-weight: bold;">${comp.title || 'Welcome to Our Website'}</h1>
                <p style="font-size: 1.25rem; margin-bottom: 2rem; opacity: 0.9;">${comp.subtitle || 'Create something amazing with our website builder'}</p>
                <button style="background: white; color: #333; padding: 12px 24px; border: none; border-radius: 8px; font-size: 1.1rem; cursor: pointer;">${comp.buttonText || 'Get Started'}</button>
              </div>
            </section>
          `;
        case 'text':
          return `
            <section style="padding: 60px 20px; background: white;">
              <div class="container" style="max-width: 800px; margin: 0 auto;">
                <h2 style="font-size: 2.5rem; margin-bottom: 1rem; color: #333;">${comp.title || 'Section Title'}</h2>
                <p style="font-size: 1.1rem; line-height: 1.6; color: #666;">${comp.content || 'Your content goes here. This is a sample text block that you can customize with your own content.'}</p>
              </div>
            </section>
          `;
        case 'gallery':
          return `
            <section style="padding: 60px 20px; background: #f8f9fa;">
              <div class="container" style="max-width: 1200px; margin: 0 auto;">
                <h2 style="text-align: center; font-size: 2.5rem; margin-bottom: 3rem; color: #333;">${comp.title || 'Image Gallery'}</h2>
                <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 20px;">
                  ${comp.images ? comp.images.map(img => `
                    <div style="background: white; border-radius: 12px; overflow: hidden; box-shadow: 0 4px 6px rgba(0,0,0,0.1);">
                      <img src="${img.url || 'https://via.placeholder.com/400x300'}" alt="${img.alt || 'Gallery Image'}" style="width: 100%; height: 250px; object-fit: cover;">
                      <div style="padding: 20px;">
                        <h3 style="margin: 0 0 10px 0; color: #333;">${img.title || 'Image Title'}</h3>
                        <p style="margin: 0; color: #666;">${img.description || 'Image description goes here.'}</p>
                      </div>
                    </div>
                  `).join('') : '<p style="text-align: center; color: #666;">No images added yet</p>'}
                </div>
              </div>
            </section>
          `;
        case 'contact':
          return `
            <section style="padding: 60px 20px; background: white;">
              <div class="container" style="max-width: 600px; margin: 0 auto;">
                <h2 style="text-align: center; font-size: 2.5rem; margin-bottom: 3rem; color: #333;">${comp.title || 'Contact Us'}</h2>
                <form style="display: flex; flex-direction: column; gap: 20px;">
                  <input type="text" placeholder="Your Name" style="padding: 15px; border: 2px solid #e1e5e9; border-radius: 8px; font-size: 1rem;">
                  <input type="email" placeholder="Your Email" style="padding: 15px; border: 2px solid #e1e5e9; border-radius: 8px; font-size: 1rem;">
                  <textarea placeholder="Your Message" rows="5" style="padding: 15px; border: 2px solid #e1e5e9; border-radius: 8px; font-size: 1rem; resize: vertical;"></textarea>
                  <button type="submit" style="background: #0ea5e9; color: white; padding: 15px; border: none; border-radius: 8px; font-size: 1.1rem; cursor: pointer;">${comp.buttonText || 'Send Message'}</button>
                </form>
              </div>
            </section>
          `;
        default:
          return '';
      }
    }).join('');

    return `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Your Website</title>
    <style>
        * { margin: 0; padding: 0; box-sizing: border-box; }
        body { font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif; line-height: 1.6; }
        .container { max-width: 1200px; margin: 0 auto; padding: 0 20px; }
        button:hover { opacity: 0.9; }
        @media (max-width: 768px) {
            h1 { font-size: 2rem !important; }
            h2 { font-size: 2rem !important; }
            .hero-section { padding: 40px 20px !important; }
        }
    </style>
</head>
<body>
    ${componentHTML}
</body>
</html>`;
  };

  const value = {
    components,
    selectedComponent,
    setSelectedComponent,
    addComponent,
    updateComponent,
    deleteComponent,
    duplicateComponent,
    moveComponent,
    clearWebsite,
    exportWebsite,
    undo,
    redo,
    canUndo,
    canRedo,
    isDirty,
  };

  return (
    <WebsiteBuilderContext.Provider value={value}>
      {children}
    </WebsiteBuilderContext.Provider>
  );
};
