import React from 'react';
import { 
  Undo2, 
  Redo2, 
  Smartphone, 
  Tablet, 
  Monitor, 
  Download, 
  Save, 
  Trash2,
  Eye,
  Settings,
  Share2,
  History
} from 'lucide-react';
import { useWebsiteBuilder } from '../context/WebsiteBuilderContext';

const Toolbar = ({ previewMode, setPreviewMode }) => {
  const { 
    undo, 
    redo, 
    canUndo, 
    canRedo, 
    exportWebsite, 
    clearWebsite, 
    isDirty 
  } = useWebsiteBuilder();

  const previewModes = [
    { id: 'desktop', icon: Monitor, label: 'Desktop', width: 'w-full' },
    { id: 'tablet', icon: Tablet, label: 'Tablet', width: 'max-w-2xl' },
    { id: 'mobile', icon: Smartphone, label: 'Mobile', width: 'max-w-sm' }
  ];

  return (
    <div className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 px-3 md:px-4 py-3">
      <div className="flex items-center justify-between">
        {/* Left side - History controls */}
        <div className="flex items-center space-x-1 md:space-x-2">
          <button
            onClick={undo}
            disabled={!canUndo}
            className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            title="Undo"
          >
            <Undo2 size={18} />
          </button>
          <button
            onClick={redo}
            disabled={!canRedo}
            className="p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
            title="Redo"
          >
            <Redo2 size={18} />
          </button>
          
          <div className="w-px h-6 bg-gray-300 dark:bg-gray-600 mx-1 md:mx-2" />
          
          <div className="hidden md:flex items-center space-x-1">
            <Save size={16} className="text-gray-500" />
            <span className="text-sm text-gray-600 dark:text-gray-400">
              {isDirty ? 'Unsaved changes' : 'All changes saved'}
            </span>
            {isDirty && (
              <div className="w-2 h-2 bg-yellow-500 rounded-full animate-pulse" />
            )}
          </div>
        </div>

        {/* Center - Preview mode selector */}
        <div className="flex items-center space-x-1 bg-gray-100 dark:bg-gray-700 rounded-lg p-1">
          {previewModes.map((mode) => {
            const Icon = mode.icon;
            return (
              <button
                key={mode.id}
                onClick={() => setPreviewMode(mode.id)}
                className={`p-2 rounded-md transition-all ${
                  previewMode === mode.id
                    ? 'bg-white dark:bg-gray-600 shadow-sm text-blue-600 dark:text-blue-400'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-white'
                }`}
                title={mode.label}
              >
                <Icon size={16} />
              </button>
            );
          })}
        </div>

        {/* Right side - Actions */}
        <div className="flex items-center space-x-1 md:space-x-2">
          <button
            onClick={() => {/* TODO: Add preview functionality */}}
            className="hidden md:flex items-center space-x-2 px-3 py-2 text-sm font-medium text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg transition-colors"
            title="Preview"
          >
            <Eye size={16} />
            <span>Preview</span>
          </button>
          
          <button
            onClick={exportWebsite}
            className="flex items-center space-x-2 px-3 py-2 text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-lg transition-colors"
            title="Export Website"
          >
            <Download size={16} />
            <span>Export</span>
          </button>
          
          <button
            onClick={() => {/* TODO: Add share functionality */}}
            className="hidden md:flex p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
            title="Share"
          >
            <Share2 size={18} />
          </button>
          
          <div className="hidden md:block w-px h-6 bg-gray-300 dark:bg-gray-600 mx-2" />
          
          <button
            onClick={() => {/* TODO: Add settings modal */}}
            className="hidden md:flex p-2 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-700 transition-colors"
            title="Settings"
          >
            <Settings size={18} />
          </button>
          
          <button
            onClick={clearWebsite}
            className="p-2 rounded-lg hover:bg-red-50 dark:hover:bg-red-900/20 text-red-600 hover:text-red-700 transition-colors"
            title="Clear Website"
          >
            <Trash2 size={18} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Toolbar;
