import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Layers, 
  Image, 
  Type, 
  Mail, 
  Star,
  ChevronDown,
  ChevronRight,
  FileText,
  Grid3X3
} from 'lucide-react';
import { useWebsiteBuilder } from '../context/WebsiteBuilderContext';
import ComponentBlock from './ComponentBlock';

const Sidebar = () => {
  const { addComponent, clearWebsite } = useWebsiteBuilder();
  const [activeTab, setActiveTab] = useState('components');
  const [expandedSections, setExpandedSections] = useState({
    basic: true,
    media: true,
    forms: true,
  });

  const toggleSection = (section) => {
    setExpandedSections(prev => ({
      ...prev,
      [section]: !prev[section]
    }));
  };

  const componentTemplates = {
    basic: [
      {
        type: 'hero',
        name: 'Hero Section',
        icon: <Star size={20} />,
        description: 'Eye-catching header with title and call-to-action',
        defaultData: {
          title: 'Welcome to Our Website',
          subtitle: 'Create something amazing with our website builder',
          buttonText: 'Get Started',
          background: '#667eea',
          backgroundSecondary: '#764ba2'
        }
      },
      {
        type: 'hero-video',
        name: 'Video Hero',
        icon: <Star size={20} />,
        description: 'Hero section with background video',
        defaultData: {
          title: 'Amazing Video Background',
          subtitle: 'Create stunning visual impact',
          buttonText: 'Watch More',
          videoUrl: 'https://sample-videos.com/zip/10/mp4/SampleVideo_1280x720_1mb.mp4'
        }
      },
      {
        type: 'hero-split',
        name: 'Split Hero',
        icon: <Star size={20} />,
        description: 'Hero with split content and image',
        defaultData: {
          title: 'Split Layout Hero',
          subtitle: 'Content on one side, image on the other',
          buttonText: 'Learn More',
          imageUrl: 'https://via.placeholder.com/600x400',
          imagePosition: 'right'
        }
      },
      {
        type: 'text',
        name: 'Text Block',
        icon: <Type size={20} />,
        description: 'Simple text content with title and body',
        defaultData: {
          title: 'Section Title',
          content: 'Your content goes here. This is a sample text block that you can customize with your own content.'
        }
      },
      {
        type: 'text-two-column',
        name: 'Two Column Text',
        icon: <Type size={20} />,
        description: 'Text content in two columns',
        defaultData: {
          title: 'Two Column Layout',
          leftContent: 'Left column content goes here...',
          rightContent: 'Right column content goes here...'
        }
      },
      {
        type: 'text-with-image',
        name: 'Text with Image',
        icon: <Type size={20} />,
        description: 'Text content with accompanying image',
        defaultData: {
          title: 'Text with Image',
          content: 'Your content with an image alongside it.',
          imageUrl: 'https://via.placeholder.com/400x300',
          imagePosition: 'right'
        }
      },
      {
        type: 'grid',
        name: 'Feature Grid',
        icon: <Grid3X3 size={20} />,
        description: 'Grid layout for features or services',
        defaultData: {
          title: 'Our Features',
          features: [
            { title: 'Feature 1', description: 'Description of feature 1', icon: 'ðŸš€' },
            { title: 'Feature 2', description: 'Description of feature 2', icon: 'âš¡' },
            { title: 'Feature 3', description: 'Description of feature 3', icon: 'ðŸ’¡' }
          ]
        }
      },
      {
        type: 'grid-4',
        name: '4-Column Grid',
        icon: <Grid3X3 size={20} />,
        description: 'Four column feature grid',
        defaultData: {
          title: 'Four Column Features',
          features: [
            { title: 'Feature 1', description: 'Description 1', icon: 'ðŸŽ¯' },
            { title: 'Feature 2', description: 'Description 2', icon: 'ðŸš€' },
            { title: 'Feature 3', description: 'Description 3', icon: 'âš¡' },
            { title: 'Feature 4', description: 'Description 4', icon: 'ðŸ’¡' }
          ]
        }
      },
      {
        type: 'stats',
        name: 'Statistics',
        icon: <Grid3X3 size={20} />,
        description: 'Display key statistics or numbers',
        defaultData: {
          title: 'Our Numbers',
          stats: [
            { number: '1000+', label: 'Happy Clients' },
            { number: '500+', label: 'Projects Completed' },
            { number: '99%', label: 'Satisfaction Rate' }
          ]
        }
      },
      {
        type: 'testimonials',
        name: 'Testimonials',
        icon: <Grid3X3 size={20} />,
        description: 'Customer reviews and testimonials',
        defaultData: {
          title: 'What Our Clients Say',
          testimonials: [
            { name: 'John Doe', role: 'CEO', company: 'Tech Corp', content: 'Amazing service and results!' },
            { name: 'Jane Smith', role: 'Founder', company: 'Startup Inc', content: 'Exceeded all expectations!' }
          ]
        }
      }
    ],
    media: [
      {
        type: 'gallery',
        name: 'Image Gallery',
        icon: <Image size={20} />,
        description: 'Grid of images with titles and descriptions',
        defaultData: {
          title: 'Image Gallery',
          images: [
            { url: 'https://via.placeholder.com/400x300', title: 'Image 1', description: 'Description for image 1' },
            { url: 'https://via.placeholder.com/400x300', title: 'Image 2', description: 'Description for image 2' }
          ]
        }
      },
      {
        type: 'gallery-masonry',
        name: 'Masonry Gallery',
        icon: <Image size={20} />,
        description: 'Pinterest-style masonry layout',
        defaultData: {
          title: 'Masonry Gallery',
          images: [
            { url: 'https://via.placeholder.com/400x300', title: 'Image 1', height: '300' },
            { url: 'https://via.placeholder.com/400x400', title: 'Image 2', height: '400' },
            { url: 'https://via.placeholder.com/400x250', title: 'Image 3', height: '250' }
          ]
        }
      },
      {
        type: 'gallery-carousel',
        name: 'Carousel Gallery',
        icon: <Image size={20} />,
        description: 'Sliding image carousel',
        defaultData: {
          title: 'Image Carousel',
          images: [
            { url: 'https://via.placeholder.com/800x400', title: 'Slide 1' },
            { url: 'https://via.placeholder.com/800x400', title: 'Slide 2' },
            { url: 'https://via.placeholder.com/800x400', title: 'Slide 3' }
          ]
        }
      },
      {
        type: 'video-section',
        name: 'Video Section',
        icon: <Image size={20} />,
        description: 'Video content with description',
        defaultData: {
          title: 'Watch Our Video',
          description: 'Learn more about our services',
          videoUrl: 'https://sample-videos.com/zip/10/mp4/SampleVideo_1280x720_1mb.mp4',
          thumbnailUrl: 'https://via.placeholder.com/800x450'
        }
      },
      {
        type: 'image-text-overlay',
        name: 'Image with Text Overlay',
        icon: <Image size={20} />,
        description: 'Image with text overlay',
        defaultData: {
          title: 'Overlay Title',
          subtitle: 'Overlay subtitle text',
          imageUrl: 'https://via.placeholder.com/800x500',
          overlayColor: 'rgba(0,0,0,0.5)'
        }
      }
    ],
    forms: [
      {
        type: 'contact',
        name: 'Contact Form',
        icon: <Mail size={20} />,
        description: 'Contact form with name, email, and message fields',
        defaultData: {
          title: 'Contact Us',
          buttonText: 'Send Message'
        }
      },
      {
        type: 'newsletter',
        name: 'Newsletter Signup',
        icon: <Mail size={20} />,
        description: 'Email newsletter subscription form',
        defaultData: {
          title: 'Stay Updated',
          subtitle: 'Subscribe to our newsletter for the latest updates',
          buttonText: 'Subscribe',
          placeholder: 'Enter your email'
        }
      },
      {
        type: 'contact-advanced',
        name: 'Advanced Contact',
        icon: <Mail size={20} />,
        description: 'Full contact form with multiple fields',
        defaultData: {
          title: 'Get In Touch',
          subtitle: 'We\'d love to hear from you',
          buttonText: 'Send Message',
          fields: ['name', 'email', 'phone', 'company', 'message']
        }
      }
    ],
    navigation: [
      {
        type: 'navbar',
        name: 'Navigation Bar',
        icon: <Layers size={20} />,
        description: 'Main navigation menu',
        defaultData: {
          logo: 'Your Logo',
          menuItems: ['Home', 'About', 'Services', 'Contact'],
          ctaButton: 'Get Started'
        }
      },
      {
        type: 'breadcrumbs',
        name: 'Breadcrumbs',
        icon: <Layers size={20} />,
        description: 'Navigation breadcrumbs',
        defaultData: {
          items: ['Home', 'Products', 'Category', 'Current Page']
        }
      },
      {
        type: 'pagination',
        name: 'Pagination',
        icon: <Layers size={20} />,
        description: 'Page navigation numbers',
        defaultData: {
          currentPage: 1,
          totalPages: 10
        }
      }
    ],
    interactive: [
      {
        type: 'accordion',
        name: 'Accordion',
        icon: <Layers size={20} />,
        description: 'Collapsible content sections',
        defaultData: {
          title: 'Frequently Asked Questions',
          items: [
            { question: 'Question 1?', answer: 'Answer to question 1' },
            { question: 'Question 2?', answer: 'Answer to question 2' },
            { question: 'Question 3?', answer: 'Answer to question 3' }
          ]
        }
      },
      {
        type: 'tabs',
        name: 'Tabs',
        icon: <Layers size={20} />,
        description: 'Tabbed content interface',
        defaultData: {
          tabs: [
            { title: 'Tab 1', content: 'Content for tab 1' },
            { title: 'Tab 2', content: 'Content for tab 2' },
            { title: 'Tab 3', content: 'Content for tab 3' }
          ]
        }
      },
      {
        type: 'timeline',
        name: 'Timeline',
        icon: <Layers size={20} />,
        description: 'Vertical timeline layout',
        defaultData: {
          title: 'Our Journey',
          events: [
            { year: '2020', title: 'Company Founded', description: 'Started our journey' },
            { year: '2021', title: 'First Product', description: 'Launched our first product' },
            { year: '2022', title: 'Expansion', description: 'Grew to 50 employees' }
          ]
        }
      }
    ],
    business: [
      {
        type: 'pricing',
        name: 'Pricing Tables',
        icon: <Grid3X3 size={20} />,
        description: 'Service pricing comparison',
        defaultData: {
          title: 'Choose Your Plan',
          plans: [
            { name: 'Basic', price: '$29', features: ['Feature 1', 'Feature 2', 'Feature 3'] },
            { name: 'Pro', price: '$79', features: ['All Basic', 'Feature 4', 'Feature 5'] },
            { name: 'Enterprise', price: '$199', features: ['All Pro', 'Feature 6', 'Feature 7'] }
          ]
        }
      },
      {
        type: 'team',
        name: 'Team Section',
        icon: <Grid3X3 size={20} />,
        description: 'Team member profiles',
        defaultData: {
          title: 'Meet Our Team',
          members: [
            { name: 'John Doe', role: 'CEO', image: 'https://via.placeholder.com/200x200', bio: 'Company leader' },
            { name: 'Jane Smith', role: 'CTO', image: 'https://via.placeholder.com/200x200', bio: 'Tech expert' }
          ]
        }
      },
      {
        type: 'clients',
        name: 'Client Logos',
        icon: <Grid3X3 size={20} />,
        description: 'Client company logos',
        defaultData: {
          title: 'Trusted By',
          logos: [
            { name: 'Client 1', logo: 'https://via.placeholder.com/150x80' },
            { name: 'Client 2', logo: 'https://via.placeholder.com/150x80' },
            { name: 'Client 3', logo: 'https://via.placeholder.com/150x80' }
          ]
        }
      }
    ],
    ecommerce: [
      {
        type: 'product-grid',
        name: 'Product Grid',
        icon: <Grid3X3 size={20} />,
        description: 'Product catalog display',
        defaultData: {
          title: 'Our Products',
          products: [
            { name: 'Product 1', price: '$99', image: 'https://via.placeholder.com/300x300', description: 'Product description' },
            { name: 'Product 2', price: '$149', image: 'https://via.placeholder.com/300x300', description: 'Product description' }
          ]
        }
      },
      {
        type: 'product-detail',
        name: 'Product Detail',
        icon: <Grid3X3 size={20} />,
        description: 'Detailed product view',
        defaultData: {
          name: 'Product Name',
          price: '$199',
          description: 'Detailed product description goes here...',
          images: ['https://via.placeholder.com/400x400', 'https://via.placeholder.com/400x400'],
          features: ['Feature 1', 'Feature 2', 'Feature 3']
        }
      }
    ],
    social: [
      {
        type: 'social-feed',
        name: 'Social Feed',
        icon: <Grid3X3 size={20} />,
        description: 'Social media content feed',
        defaultData: {
          title: 'Follow Us',
          platform: 'Instagram',
          posts: [
            { image: 'https://via.placeholder.com/300x300', caption: 'Amazing post!', likes: '1.2k' },
            { image: 'https://via.placeholder.com/300x300', caption: 'Another great post!', likes: '856' }
          ]
        }
      }
    ]
  };

  const starterTemplates = [
    {
      name: 'Business Landing',
      description: 'Professional business website template',
      components: [
        { type: 'hero', title: 'Welcome to Our Business', subtitle: 'We provide innovative solutions for your needs' },
        { type: 'text', title: 'About Us', content: 'We are a leading company in our industry...' },
        { type: 'contact', title: 'Get In Touch' }
      ]
    },
    {
      name: 'Portfolio Showcase',
      description: 'Showcase your work with style',
      components: [
        { type: 'hero', title: 'Creative Portfolio', subtitle: 'Showcasing amazing work and projects' },
        { type: 'gallery', title: 'My Work', images: [] },
        { type: 'contact', title: 'Let\'s Work Together' }
      ]
    },
    {
      name: 'Simple Blog',
      description: 'Clean and minimal blog layout',
      components: [
        { type: 'hero', title: 'My Blog', subtitle: 'Thoughts, ideas, and stories' },
        { type: 'text', title: 'Latest Post', content: 'This is where your blog content would go...' }
      ]
    }
  ];

  const handleAddComponent = (template) => {
    addComponent({
      ...template,
      id: Date.now().toString(),
      ...template.defaultData
    });
  };

  const loadTemplate = (template) => {
    clearWebsite();
    template.components.forEach(comp => {
      addComponent({
        ...comp,
        id: Date.now().toString() + Math.random(),
        ...comp.defaultData
      });
    });
  };

  return (
    <div className="h-full sidebar-panel overflow-y-auto scrollbar-hide">
      {/* Tabs */}
      <div className="flex border-b border-gray-200 dark:border-gray-700">
        <button
          onClick={() => setActiveTab('components')}
          className={`flex-1 py-3 px-4 text-sm font-medium transition-colors duration-200 ${
            activeTab === 'components'
              ? 'text-primary-600 border-b-2 border-primary-600'
              : 'text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-300'
          }`}
        >
          <Layers size={16} className="inline mr-2" />
          Components
        </button>
        <button
          onClick={() => setActiveTab('templates')}
          className={`flex-1 py-3 px-4 text-sm font-medium transition-colors duration-200 ${
            activeTab === 'templates'
              ? 'text-primary-600 border-b-2 border-primary-600'
              : 'text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-300'
          }`}
        >
          <FileText size={16} className="inline mr-2" />
          Templates
        </button>
      </div>

      <div className="p-4">
        <AnimatePresence mode="wait">
          {activeTab === 'components' && (
            <motion.div
              key="components"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 20 }}
              transition={{ duration: 0.2 }}
            >
              <div className="space-y-4">
                {Object.entries(componentTemplates).map(([sectionKey, sectionComponents]) => (
                  <div key={sectionKey} className="space-y-2">
                    <button
                      onClick={() => toggleSection(sectionKey)}
                      className="flex items-center justify-between w-full text-left text-sm font-medium text-gray-700 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white transition-colors duration-200"
                    >
                      <span className="capitalize">{sectionKey}</span>
                      {expandedSections[sectionKey] ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
                    </button>
                    
                    <AnimatePresence>
                      {expandedSections[sectionKey] && (
                        <motion.div
                          initial={{ height: 0, opacity: 0 }}
                          animate={{ height: 'auto', opacity: 1 }}
                          exit={{ height: 0, opacity: 0 }}
                          transition={{ duration: 0.2 }}
                          className="space-y-2 pl-4"
                        >
                          {sectionComponents.map((template, index) => (
                            <ComponentBlock
                              key={index}
                              template={template}
                              onAdd={() => handleAddComponent(template)}
                            />
                          ))}
                        </motion.div>
                      )}
                    </AnimatePresence>
                  </div>
                ))}
              </div>
            </motion.div>
          )}

          {activeTab === 'templates' && (
            <motion.div
              key="templates"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 20 }}
              transition={{ duration: 0.2 }}
            >
              <div className="space-y-4">
                <div className="text-sm text-gray-600 dark:text-gray-400 mb-4">
                  Choose a starter template to get started quickly
                </div>
                
                {starterTemplates.map((template, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    className="card p-4 cursor-pointer hover:shadow-medium transition-shadow duration-200"
                    onClick={() => loadTemplate(template)}
                  >
                    <h3 className="font-medium text-gray-900 dark:text-white mb-1">
                      {template.name}
                    </h3>
                    <p className="text-sm text-gray-600 dark:text-gray-400 mb-3">
                      {template.description}
                    </p>
                    <div className="flex items-center text-xs text-gray-500 dark:text-gray-400">
                      <Layers size={14} className="mr-1" />
                      {template.components.length} components
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default Sidebar;
